var arr = macro("$IcingaCheckHTTPStatus_Array_StatusCode$");

if (len(arr) == 0) {
    return "@()";
}

var psarr = arr.map(
    x => if (typeof(x) == String) {
        var argLen = len(x);
        if (argLen != 0 && x.substr(0,1) == "'" && x.substr(argLen - 1, argLen) == "'") {
            x;
        } else {
            "'" + x + "'";
        }
    } else {
        x;
    }
).join(",");

return "@(" + psarr + ")";

